// components/Register.js: This is the React component for the registration form

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Register.css';

const Register = () => {
  // Define the state variables for the user input and validation
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [age, setAge] = useState('');
  const [batch, setBatch] = useState('');
  const [errors, setErrors] = useState({});

  // Define the state variable for the API response
  const [response, setResponse] = useState(null);

  // Get the history object from React Router
  const history = useNavigate();

  // Define a function to handle the form submission
  const handleSubmit = (e) => {
    // Prevent the default browser behavior
    e.preventDefault();

    // Clear any previous errors
    setErrors({});

    // Validate the user input
    let isValid = true;
    if (!name) {
      setErrors((prev) => ({ ...prev, name: 'Please enter your name' }));
      isValid = false;
    }
    if (!email) {
      setErrors((prev) => ({ ...prev, email: 'Please enter your email' }));
      isValid = false;
    }
    if (!password) {
      setErrors((prev) => ({ ...prev, password: 'Please enter your password' }));
      isValid = false;
    }
    if (!age) {
      setErrors((prev) => ({ ...prev, age: 'Please enter your age' }));
      isValid = false;
    } else if (age < 18 || age > 65) {
      setErrors((prev) => ({ ...prev, age: 'Age limit is 18-65' }));
      isValid = false;
    }
    if (!batch) {
      setErrors((prev) => ({ ...prev, batch: 'Please select a batch' }));
      isValid = false;
    }

    // If the input is valid, make the API request
    if (isValid) {
      axios
        .post('/api/register', { name, email, password, age, batch })
        .then((res) => {
          // Set the response state
          setResponse(res.data);
        })
        .catch((err) => {
          // Set the error state
          setErrors(err.response.data);
        });
    }
  };

  // Use the useEffect hook to handle the API response
  useEffect(() => {
    // If the response is successful, store the user's token and data in the local storage
    if (response) {
      localStorage.setItem('token', response.token);
      localStorage.setItem('user', JSON.stringify(response.user));

      // Redirect to the home page
      useNavigate('/');
    }
  }, [response, history]);

  return (
    <div className="register">
      <h2>Register</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          {errors.name && <span className="error">{errors.name}</span>}
        </div>
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          {errors.email && <span className="error">{errors.email}</span>}
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          {errors.password && <span className="error">{errors.password}</span>}
        </div>
        <div className="form-group">
          <label htmlFor="age">Age</label>
          <input
            type="number"
            id="age"
            value={age}
            onChange={(e) => setAge(e.target.value)}
          />
          {errors.age && <span className="error">{errors.age}</span>}
        </div>
        <div className="form-group">
          <label htmlFor="batch">Batch</label>
          <select
            id="batch"
            value={batch}
            onChange={(e) => setBatch(e.target.value)}
          >
            <option value="">Select a batch</option>
            <option value="6-7AM">6-7AM</option>
            <option value="7-8AM">7-8AM</option>
            <option value="8-9AM">8-9AM</option>
            <option value="5-6PM">5-6PM</option>
          </select>
          {errors.batch && <span className="error">{errors.batch}</span>}
        </div>
        <div className="form-group">
          <button type="submit">Register</button>
        </div>
      </form>
    </div>
  );
};

export default Register;
